
public class SwapTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=30,temp;
		System.out.println("before swapping a = " + a +" b = " + b);
		temp=a;
		a=b;    
		b=temp;
		System.out.println("after swapping a = " + a + " b = " + b);
	}

}
