
public class NumBelow100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=99;
		Below100(num1);
		int num2=4;
		Below100(num2);
		int num3=105;
		Below100(num3);
		
	}
	static void Below100(int num) {
		if(num<100)
			System.out.println(num + " is below 100");
	
}
}